find ~/projet/data -name "*.sysdyn" -fprint ~/projet/data/data_search; string=$(grep -c ".sysdyn" ~/projet/data/data_search);if [ "$string" -eq 0 ]; then echo "No regeistered dynamic systems were found"; elif [ -n "$string" ]; then echo "$string system(s were found" && echo $(cat ~/projet/data/data_search) && echo "Would you like to edit or view any of them?" && read choice;if [ "$choice" == "oui" ] || [ "$choice" == "Oui" ]; then echo "lequel?" && read cmd; echo $($cmd);else ./start.o; fi; fi